CREATE TABLE public.product ( id integer NOT NULL, category character varying(255) COLLATE pg_catalog."default", created_date date, description character varying(255) COLLATE pg_catalog."default", last_modified_date date, name character varying(255) COLLATE pg_catalog."default", quantity integer NOT NULL, CONSTRAINT product_pkey PRIMARY KEY (id) )
TABLESPACE pg_default;
ALTER TABLE public.product OWNER to postgres;
